import React, { useState, useEffect } from 'react';

const initialTour = {
    title: '',
    city: '',
    address: '',
    distance: '',
    price: '',
    maxGroupSize: '',
    desc: '',
    photo: '',
    featured: false,
};

const TourManagement = () => {
    const [tours, setTours] = useState([]);
    const [newTour, setNewTour] = useState(initialTour);
    const [editingTour, setEditingTour] = useState(null);

    useEffect(() => {
        const fetchTours = async () => {
            const response = await fetch('/api/tours');
            const data = await response.json();
            setTours(data);
        };
        fetchTours();
    }, []);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setNewTour({
            ...newTour,
            [name]: type === 'checkbox' ? checked : value,
        });
    };

    const handleAddTour = async () => {
        const response = await fetch('/api/tours', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(newTour),
        });
        const addedTour = await response.json();
        setTours([...tours, addedTour]);
        setNewTour(initialTour);
    };

    const handleEditTour = (tour) => {
        setEditingTour(tour);
        setNewTour(tour);
    };

    const handleUpdateTour = async () => {
        const response = await fetch(`/api/tours/${editingTour._id || editingTour.id}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(newTour),
        });
        const updatedTour = await response.json();
        setTours(tours.map(tour => (tour._id === updatedTour._id ? updatedTour : tour)));
        setEditingTour(null);
        setNewTour(initialTour);
    };

    const handleDeleteTour = async (id) => {
        await fetch(`/api/tours/${id}`, { method: 'DELETE' });
        setTours(tours.filter(tour => (tour._id || tour.id) !== id));
    };

    return (
        <div>
            <h2>Quản lý Tour Du Lịch</h2>
            <div>
                <input name="title" placeholder="Tên tour" value={newTour.title} onChange={handleChange} />
                <input name="city" placeholder="Tỉnh/Thành phố" value={newTour.city} onChange={handleChange} />
                <input name="address" placeholder="Địa chỉ" value={newTour.address} onChange={handleChange} />
                <input name="distance" type="number" placeholder="Khoảng cách" value={newTour.distance} onChange={handleChange} />
                <input name="price" type="number" placeholder="Giá" value={newTour.price} onChange={handleChange} />
                <input name="maxGroupSize" type="number" placeholder="Nhóm tối đa" value={newTour.maxGroupSize} onChange={handleChange} />
                <input name="photo" placeholder="Link ảnh" value={newTour.photo} onChange={handleChange} />
                <textarea name="desc" placeholder="Mô tả" value={newTour.desc} onChange={handleChange} />
                <label>
                    <input name="featured" type="checkbox" checked={newTour.featured} onChange={handleChange} />
                    Nổi bật
                </label>
                {editingTour ? (
                    <button onClick={handleUpdateTour}>Cập nhật</button>
                ) : (
                    <button onClick={handleAddTour}>Thêm tour</button>
                )}
            </div>
            <table>
                <thead>
                    <tr>
                        <th>Tên tour</th>
                        <th>Thành phố</th>
                        <th>Địa chỉ</th>
                        <th>Khoảng cách</th>
                        <th>Giá</th>
                        <th>Nhóm tối đa</th>
                        <th>Nổi bật</th>
                        <th>Ảnh</th>
                        <th>Mô tả</th>
                        <th>Thao tác</th>
                    </tr>
                </thead>
                <tbody>
                    {tours.map(tour => (
                        <tr key={tour._id || tour.id}>
                            <td>{tour.title}</td>
                            <td>{tour.city}</td>
                            <td>{tour.address}</td>
                            <td>{tour.distance}</td>
                            <td>{tour.price}</td>
                            <td>{tour.maxGroupSize}</td>
                            <td>{tour.featured ? '✔' : ''}</td>
                            <td>
                                {tour.photo && <img src={tour.photo} alt={tour.title} width={50} />}
                            </td>
                            <td style={{maxWidth: 200, overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap'}}>{tour.desc}</td>
                            <td>
                                <button onClick={() => handleEditTour(tour)}>Sửa</button>
                                <button onClick={() => handleDeleteTour(tour._id || tour.id)}>Xóa</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default TourManagement;
