import React, {useRef, useEffect} from 'react';

import {Container, Row, Button} from 'reactstrap'
import { NavLink, Link } from 'react-router-dom';

import logo from '../../assets/images/logo.png'
import "./header.css";

const nav__links = [
  {
    path: '/home',
    display: 'Trang chủ'
  },
  {
    path: '/about',
    display: 'Giới thiệu'
  },
  {
    path: '/tours',
    display: 'Tours du lịch'
  },
];

const Header = () => {

  // const headerRef = useRef(null)

  // const stickyHeaderFunc = ()=>{
  //   window.addEvenListener('scroll', ()=>{
  //     if(document.body.scrollTop > 80 || document.documentElement.scrollTop > 80){
  //       headerRef.current.classList.add('sticky__header')
  //     }
  //     else{
  //       headerRef.current.classList.remove('stcky__header')
  //     }
  //   });
  // };

  // useEffect(() => {
  // stickyHeaderFunc();

  // return window.removeEventListener('scroll', stickyHeaderFunc);
  // });

  return (
    <header className="header">  
      <Container>
        <Row>
          <div className="nav__wrapper d-flex align-items-center justify-content-between">

            {/* ============== logo ============= */}
            <div className="logo">
              <div className="logo">
                <img src={logo} alt="" />
              </div>
            </div>
            {/* ============== logo end ============= */}

            {/* ============== menu start ============= */}
            <div className="navigation">
              <ul className="menu d-flex algin-items-center gap-5">
                {
                  nav__links.map((item, index) => (
                    <li className="nav__item" key={index}>
                      <NavLink
                        to={item.path} className={navClass=>
                          navClass.isActive ? "active__link" : ""
                        }
                      >
                        {item.display}
                      </NavLink>
                    </li>
                  ))
                } 
              </ul>
            </div>
            {/* ============== mwnu end ============= */}

            <div className="nav__right d-flex align-items-center gap-4 "> 
              <div className="nav__btns d-flex align-items-center gap-4">
                <Button className="btn secondary__btn"><Link to='/login'>Đăng nhập</Link></Button>
                <Button className="btn primary__btn"><Link to='/register'>Đăng ký</Link></Button>
              </div>
            </div>

            <span className="mobile__menu"> 
              <i class="ri-menu-line"></i>
            </span>

          </div>
        </Row>
      </Container>
    </header>
  )
}

export default Header
